'use stricts'

const button_left = document.querySelector('.posts__slider_button-icon--left');
const button_right = document.querySelector('.posts__slider_button-icon--right');
const slider = document.querySelector('.posts__slider');
const sliderItems = document.getElementsByClassName('posts__slider_items');
const sliderItemsLenght = sliderItems.length;


for (let i = 0; i < sliderItemsLenght; i++) {

    sliderItems[i].onclick = function() {

        let it = sliderItems[0];

        while (it) {
            if (it.tagName === 'LI') {
                it.classList.remove('posts__slider_items--active');
            }
            it = it.nextSibling;
        }

        this.classList.add('posts__slider_items--active');
    };

}